import React from 'react'
import { Grid } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';


import MailPic from '../../img/MailImage.png'


const useStyles = makeStyles((theme) => ({
    signUpPage: {
        background: 'white',
        backgroundSize: 'cover',
        textAlign: 'left',
        maxWidth: '100%',
        color:'black',
        minHeight: '85vh',
        minWidth: '100%',
        position:'relative',
        display: 'flex',
        flexDirection: 'column',
    },
    viewButton:{
        padding:'20px 0',
        width: '256px',
        border:'0',
        borderRadius: '50px',
        fontSize: 'x-large',
        marginTop: '32px',
        backgroundColor:'#F2F2F2',
    },
    mailImage:{
        height: '500px',
    },
    mainTitle: {
        [theme.breakpoints.down('sm')]: {
            fontSize: '35px',
            textAlign:'center',
        },
        [theme.breakpoints.up('md')]: {
            fontSize: '72px',
            fontWeight: 'bold',
            paddingTop: '5%',
            position: 'relative',
        }
    },
    subHeader: {
        [theme.breakpoints.down('sm')]: {
            letterSpacing: '8px',
            fontSize: '25px',
            textAlign:'center',
        },
        [theme.breakpoints.up('md')]: {
            letterSpacing: '8px',
            fontSize: '30px',
            paddingTop: '32px',
        }
    },
    explanation: {
        [theme.breakpoints.down('sm')]: {
            marginTop: '16px',
            marginBottom:'16px',
            fontSize: '20px',
            textAlign:'center',
        },
        [theme.breakpoints.up('md')]: {
            paddingTop: '32px',
            fontSize: '50px',
        }
    },
}));
export default function SignUp() {
    const classes = useStyles();
    return (
        <div className = {classes.signUpPage}>

        <Grid  container direction="row" justify="space-between" alignItems="center" className="blogGrid">
            <Grid item xs={4} md = {6}>
               <img src={MailPic} alt="mailImage" className={classes.mailImage} /> 
            </Grid>
           <Grid item xs={4} md={5}>
               <div className={classes.mainTitle}>SIGN UP </div>
               <div className={classes.explanation}>TO OUR MAILING LIST</div>
               <button className={classes.viewButton}>Sign Up</button>
           </Grid>
       </Grid>
   </div>
    )
}
