import React, { Component } from 'react'

import FetchService from '../helper/FetchService'
import EventComponent from './subComponents/EventComponent';

export default class EventDetail extends Component {

    constructor() {
        super();
        this.state = {
            responseObject: {},
            loading: true,
            formattedObject: {}
        }
    }

    componentDidMount() {
        console.log("Mount");
        this.getGuestDetails();
    }

    getGuestDetails() {
        FetchService.GET("https://spreadsheets.google.com/feeds/list/1yBWFVVbVmwLQEtjBSWNj4ioaoVhUZxv2NNm4VN1BUrY/od6/public/basic?alt=json")
            .then((res) => {
                // console.log(res['feed']['entry'][0]['content']['$t']);
                // this.setState({ responseObject: res['feed']['entry'] });
                this.getObject(res['feed']['entry']);
                this.setState({ loading: false });

            })
    }

    //Following code is pretty janky and hacked together. A better api structure would much help this and should be changed asap
    getObject(responseObject) {
        var stringWithInfo = responseObject[0]['content']['$t'].toString();

        console.log(stringWithInfo);
        // var dateTime = stringWithInfo.substring(stringWithInfo.indexOf('fullname: ') + 10, stringWithInfo.indexOf(', fullname'));
        var fullname = stringWithInfo.substring(stringWithInfo.indexOf('fullname: ') + 10, stringWithInfo.indexOf(', position'));
        var position = stringWithInfo.substring(stringWithInfo.indexOf(', position: ') + 12, stringWithInfo.indexOf(', image'));
        var imageURL = stringWithInfo.substring(stringWithInfo.indexOf(', image: ') + 9, stringWithInfo.indexOf(', description'));
        var description = stringWithInfo.substring(stringWithInfo.indexOf(', description: ') + 15, stringWithInfo.indexOf(', linkedin'));
        var linkedIn = stringWithInfo.substring(stringWithInfo.indexOf(', linkedin: ') + 12, stringWithInfo.indexOf(', sessiondate:'));
        var sessionDate = stringWithInfo.substring(stringWithInfo.indexOf(', sessiondate: ') + 15, stringWithInfo.indexOf(', sessiontime:'));
        var sessionTime = stringWithInfo.substring(stringWithInfo.indexOf(', sessiontime: ') + 15, stringWithInfo.length);

        // var linkedIn = stringWithInfo.substring(stringWithInfo.indexOf(', linkedin: ') + 12, stringWithInfo.length);


        var entry = { fullname, position, imageURL, description, linkedIn, sessionDate, sessionTime };
        this.setState({ formattedObject: entry });
        // listToAdd.push(entry);
        console.log("Here right now");
        console.log(this.state.formattedObject);
    }

    render() {
        return (
            <>
                <section id="events" className="events">
                    <div className="container" data-aos="fade-up">
                        <div className="row">
                            <div className="col-md-6 d-flex align-items-stretch">

                                <div className="card">
                                    <div className="card-img">
                                        <img src={require("../img/annie.png")} alt="..." />
                                    </div>
                                    <div style={{ marginTop: "0.5%" }} className="card-body">
                                        <h5 className="card-title">
                                            <a href="">  {this.state.formattedObject.fullname}
                                            </a>
                                        </h5>
                                        <p className="font-italic text-center">
                                            {this.state.formattedObject.sessionDate}, {this.state.formattedObject.sessionTime}, 2PM.
            </p>
                                        <p className="card-text">
                                            Annamarie is a third-year student at Harvard Law School. She graduated from the University of Rochester in 2018, where she studied both Business and Vocal Performance. She completed her first year of law school at Indiana University Maurer School of Law, after which she transferred to Harvard. She hopes to use her law degree to increase the participation of minority groups (particularly women and the LGBTQ+ community) in government/public policy spaces. After graduating in May 2021 she will join Kirkland and Ellis, LLP as a general corporate attorney.Being

            </p>



                                        <p>Stream the session here on October 17th, Saturday, 2PM</p>
                                        {/* <p>
                                    <a href="https://www.youtube.com/watch?v=fK0I9cZsMOg">View on YouTube</a>
                                </p>
                                <p>
                                    <a href="https://www.facebook.com/careersuncorked/posts/160499229013848">View on Facebook</a>
                                </p> */}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </>
        )
    }
}
