import React, { Component } from 'react'

export default class GuestComponent extends Component {

    constructor() {
        super();
        this.state = {
            imgURL: "",
            Name: "Tolga Aktas",
            Position: "Computer Vision SWE Intern, Google",
            Description: "Tolga graduated from the University of Rochester in 2020 after pursuing a degree in Electrical Computer Engineering, and Computer Science. He spent his time in Rochester researching digital and image processing, artificial intelligence, computer vision, robotics, machine hearing, data mining, and machine learning. Despite its rigors, he was fascinated with the amount of information carryover between these fields, and how they complemented or inspired research in one another. He spent his summer in 2019 doing ML at Qualcomm and is presently a computer vision software engineer at Google!",
        }
    }

    componentDidMount() {
        this.getData();

    }

    getData() {
        console.log("Hi" + this.props.imgURL);
        this.setState({ imgURL: this.props.imgURL });
    }

    render() {
        return (
            <div className="col-lg-4 col-md-6 d-flex align-items-stretch">
                <div className="member">
                    <img src={require("../../img/trainers/" + this.state.imgURL)} className="img-fluid" alt="" />
                    <div className="member-content">
                        <h4>{this.state.Name}</h4>
                        <span>{this.state.Position}</span>
                        <p>{this.state.Description}</p>
                    </div>
                </div>
            </div>
        );
    }
}
