import React, { Component } from 'react'
import { Link } from 'react-router-dom';

import { withRouter } from "react-router-dom"


export default class Header extends Component {
    state = {
        title: "Home"
    }

    changeTitle = (newTitle) => {
        this.setState({
            title: newTitle
        })
    }

    componentDidMount() {
        //listens for any route changes
        this.props.history.listen(() => {
            this.changeTitle(window.location.pathname)
        })
    }

    render() {
        return (
            <header id="header" className="fixed-top">
                <div className="container d-flex align-items-center">
                    <a href="/" className="logo mr-auto"><img src={require("../img/logo/logo2.png")} alt="" className="img-fluid" /></a>
                    <h1 className="logo mr-auto"><a href="/">Uncorked</a></h1>

                    <nav className="nav-menu d-none d-lg-block">
                        <ul>
                            <li className="active"><a href="/"> Let's Start</a></li>
                            <li><a href="/stories">Stories</a></li>
                            <li><a href="/events">Events</a></li>
                            <li><a href="/guests">Guests</a></li>
                            <li><a href="/blogs">Blogs</a></li>
                            <li><a href="/behindthecurtains">Behind the Curtains</a></li>

                        </ul>
                    </nav>
                    <button type="button" className="get-started-btn" data-toggle="modal" data-target="#getStarted">
                        Get Started
            </button>

                </div>
            </header>
        )
    }
}
