import React, { Component } from 'react'
import { Grid } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';


import Michelle_pic from '../../img/Michelle_pic.png'

import backgroundImage from '../../img/purpleBackground.png';


const useStyles = makeStyles((theme) => ({
    blogs: {
        backgroundImage: `url(${backgroundImage})`,
        backgroundPosition: 'center', 
        backgroundSize: 'cover', 
        backgroundRepeat: 'no-repeat',
        textAlign: 'left',
        maxWidth: '100%',
        color:'white',
        minHeight: '85vh',
        minWidth: '100%',
        position:'relative',
        display: 'flex',
        flexDirection: 'column',
    },
    blogGrid:{
        paddingTop: '5%',
    },
    mainTitle: {
        [theme.breakpoints.down('sm')]: {
            fontSize: '35px',
            textAlign :'center',
            color:'white',
        },
        [theme.breakpoints.up('md')]: {
            fontSize: '100px',
            fontWeight: 'bold',
            paddingTop: '5%',
            position: 'relative',
            textAlign :'center',
        }
    },
    explanation: {
        [theme.breakpoints.down('sm')]: {
            marginTop: '16px',
            marginBottom:'16px',
            fontSize: '20px',
            textAlign :'center',
            color:'white',
            backgroundColor: '#2c2c2c98',
        },
        [theme.breakpoints.up('md')]: {
            marginTop: '8px',
            fontSize: '24px',
            textAlign:'center',
        }
    },
    whiteline:{
        width: '50%',
    },
    viewButton:{
        [theme.breakpoints.down('sm')]: {
            padding:'20px 0',
            width: '180px',
            border:'0',
            borderRadius: '50px',
            fontSize: 'x-large',
            backgroundColor: 'rgb(253, 253, 253)',
            margin: '0 auto',
            fontSize:'18px',
        },
        [theme.breakpoints.up('md')]: {
            padding:'20px 0',
            width: '256px',
            border:'0',
            borderRadius: '50px',
            fontSize: 'x-large',
            backgroundColor: 'rgb(253, 253, 253)',
            marginTop: '32px',
        } 
    },
}));


export default function Blogs() {
    const classes = useStyles();
    return (
        <div className = {classes.blogs}>
                <Grid container direction="row" justify="center" alignItems="center" className={classes.blogGrid}>
                <Grid item md={6}>
                    <div className={classes.mainTitle}>Careers Uncorked Blogs</div>
                    <div className={classes.explanation}><p>FIND OUR INSIGNT ON THINGS FROM GETTING AN MBA TO MAKING THE MOST OF COLLEGE DURING A PANDEMIC</p>

                    </div>
                    <hr className={classes.whiteline}/>
                    <button className={classes.viewButton}>View Blogs</button>
                </Grid>
                <Grid item md={5} alignItems="center" className='co-host-info'>

                    <img src={Michelle_pic} alt="Michelle" className="michelePic" /> 
                    <div className="designation">
                        <div className="michelleName">Michelle Shue</div>
                        <div className="michelePosition">Chief Writer</div>
                    </div>

                </Grid>
            </Grid>
        </div>


    )

}
